create table emp
(
    empno    serial
        constraint emp_pkey
            primary key,
    ename    varchar(50) not null,
    job      varchar(50) not null,
    mgr      integer
        constraint emp_mgr_fkey
            references emp,
    hiredate date        not null,
    sal      integer     not null,
    comm     integer,
    deptno   integer     not null
        constraint emp_deptno_fkey
            references dept
);

alter table emp
    owner to postgres;

